import React, { useState, useEffect } from 'react'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom'; 
import '../../css/page/Pagina.css';
import CustomNavbar from '../../components/CustomNavbar_01';

function Carrusel() {
    const [carrusel, setCarrusel] = useState([]);
    const [containerWidth, setContainerWidth] = useState(window.innerWidth);
    const [containerHeight, setContainerHeight] = useState(window.innerHeight);
    const [selectedId, setSelectedId] = useState(null);
    const [datosAvisos, setDatosAvisos] = useState([]);

    useEffect(() => {
        const fetchCarrusel = async () => {
            try {
                const response = await fetch('http://localhost:8000/avisos');
                const data = await response.json();
                if (data && data.length > 0) {
                    setCarrusel(data); // Guardamos todos los avisos
                    setDatosAvisos(data); // Guardamos todos los avisos para su uso posterior
                }
            } catch (error) {
                console.error('Error al obtener el carrusel:', error);
            }
        };

        fetchCarrusel();
    }, []);

    useEffect(() => {
        const handleResize = () => {
            setContainerWidth(window.innerWidth);
            setContainerHeight(window.innerHeight);
        };

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    function calcularTamañoOptimo(containerWidth, containerHeight) {
        const maxWidth = containerWidth * 0.8;
        const maxHeight = containerHeight * 0.8;
        return { width: maxWidth * 0.29, height: maxHeight * 0.29 }; // Ajustamos las proporciones
    }

    const tamañoOptimo = calcularTamañoOptimo(containerWidth, containerHeight);

    const handleRowClick = (id_aviso) => {
        if (selectedId === id_aviso) {
            return;
        } else {
            setSelectedId(id_aviso);
            console.log("ID del aviso seleccionado:", id_aviso);
        }
    };

    const handleDelete = () => {
        if (!selectedId) {
            return;
        }

        const confirmar = window.confirm('¿Estás seguro de eliminar el aviso?');

        if (confirmar) {
            const deleteAviso = async () => {
                try {
                    const response = await fetch(`http://localhost:8000/avisos/${selectedId}`, {
                        method: 'DELETE'
                    });

                    if (response.ok) {
                        const newCarrusel = carrusel.filter(aviso => aviso.id_aviso !== selectedId);
                        setCarrusel(newCarrusel);
                        setSelectedId(null);
                    }
                } catch (error) {
                    console.error('Error al eliminar el aviso:', error);
                }
            };

            deleteAviso();
        }
    };



    return (
        <div className="pagina">
            <CustomNavbar />
            <div className="container d-flex justify-content-between align-items-center">
                    <h1 className="fs-1"><b>Carrusel</b></h1>
                    <div className="d-flex align-items-center">
                        <div className="input-group rounded-pill border border-1 me-2 custom-border">

                        </div>
                        <Link to="/pagina/carrusel/insertar" className="link-dark text-decoration-none px-2">
                            <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="#04703F" className="bi bi-plus-circle" viewBox="0 0 16 16">
                                <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                                <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4" />
                            </svg>
                        </Link>


                    </div>
                </div>
                <div className="container">
                {carrusel.length > 0 && tamañoOptimo && (
                    <div className="row">
                        {carrusel.map(aviso => (
                            <div 
                                key={aviso.id_aviso} 
                                className={`carrusel-item col-3`} 
                                style={{ 
                                    width: tamañoOptimo.width, 
                                    height: tamañoOptimo.height, 
                                    margin: '10px',
                                    cursor: 'pointer',
                                    border: selectedId === aviso.id_aviso ? '2px solid #04703F' : 'none'
                                }}
                                onClick={() => handleRowClick(aviso.id_aviso)}
                            >
                                <img 
                                    src={`${aviso.ruta}${aviso.imagen}`} 
                                    alt={aviso.imagen} 
                                    style={{ 
                                        width: '100%', 
                                        height: '100%', 
                                        objectFit: 'cover' 
                                    }} 
                                />
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}


export default Carrusel;
