import React, { useState, useEffect } from 'react'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom'; 
import '../../css/page/Pagina.css';
import CustomNavbar from '../../components/CustomNavbar_01';

function Carrusel() {
    const [carrusel, setCarrusel] = useState(null);
    const [containerWidth, setContainerWidth] = useState(window.innerWidth);
    const [containerHeight, setContainerHeight] = useState(window.innerHeight);

    useEffect(() => {
        const fetchCarrusel = async () => {
            try {
                const response = await fetch('http://localhost:8000/avisos');
                const data = await response.json();
                if (data && data.length > 0) {
                    setCarrusel(data[0]); // Asumimos que solo hay un carrusel activo
                }
            } catch (error) {
                console.error('Error al obtener el carrusel:', error);
            }
        };

        fetchCarrusel();
    }, []);

    useEffect(() => {
        const handleResize = () => {
            setContainerWidth(window.innerWidth);
            setContainerHeight(window.innerHeight);
        };

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    function calcularTamañoOptimo(carrusel, containerWidth, containerHeight) {
        if (!carrusel) return null;

        const carruselWidth = carrusel.ancho;
        const carruselHeight = carrusel.alto;

        // Calcula la relación de aspecto original del carrusel
        const aspectRatio = carruselWidth / carruselHeight;

        // Calcula el ancho y alto máximo permitido para el carrusel
        const maxWidth = containerWidth * 0.8; // Puedes ajustar este valor según tu diseño
        const maxHeight = containerHeight * 0.8; // Puedes ajustar este valor según tu diseño

        // Calcula el tamaño óptimo en función de la relación de aspecto original y las dimensiones máximas
        let width = maxWidth * 0.29; // Reducir el ancho al 40%
        let height = width / aspectRatio;

        // Verifica si la altura excede la altura máxima permitida
        if (height > maxHeight) {
            height = maxHeight * 0.29; // Reducir la altura al 40%
        }

        return { width, height };
    }

    const tamañoOptimo = calcularTamañoOptimo(carrusel, containerWidth, containerHeight);

    const handleRowClick = (id_aviso) => {
        // Verificar si la fila clicada ya está seleccionada
        if (selectedId === id_aviso) {
            return; // Si ya está seleccionada, no hacemos nada
        } else {
            setSelectedId(id_aviso); // Si no está seleccionada, la seleccionamos
            console.log("ID del aviso seleccionado:", id_aviso); // Imprimir el ID del usuario seleccionado en la consola
        }
    };

    const handleDelete = () => {
        if (selectedId) {
            const confirmDelete = window.confirm('¿Seguro que deseas eliminar este usuario?');
            if (confirmDelete) {
                fetch(`http://localhost:8000/aviso/borrar/${selectedId}`, {
                    method: 'DELETE'
                })
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Error al eliminar el usuario');
                    }
                    return response.json();
                })
                .then(data => {
                    // Actualizar la lista de Avisos después de eliminar el usuario
                    const updatedAvisos = datosAvisos.filter(usuario => usuario.id !== selectedId);
                    setDatosAvisos(updatedAvisos);
                    // Limpiar la selección
                    setSelectedId(null);
                    window.location.href = '/';
                })
                .catch(error => {
                    console.error('Error al eliminar el usuario:', error);
                });
            }
        } else {
            alert('Por favor, selecciona un usuario para eliminar.');
        }

    };


    return (
        <div className="pagina">
            <CustomNavbar />
            <div className="container d-flex justify-content-between align-items-center">
                    <h1 className="fs-1"><b>Carrusel</b></h1>
                    <div className="d-flex align-items-center">
                        <div className="input-group rounded-pill border border-1 me-2 custom-border">

                        </div>
                        <Link to="/pagina/carrusel/insertar" className="link-dark text-decoration-none px-2">
                            <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="#04703F" className="bi bi-plus-circle" viewBox="0 0 16 16">
                                <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                                <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4" />
                            </svg>
                        </Link>
                        <Link to={selectedId ? `/pagina/carrusel/actualizar?id_usuario=${selectedId}` : '#'} className="link-dark text-decoration-none px-2">
                            <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="#04703F" className="bi bi-pencil-square" viewBox="0 0 16 16">
                                <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293z" />
                                <path d="M13.752 4.396l-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z" />
                                <path fillRule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5z" />
                            </svg>
                        </Link>
                        <Link to={selectedId ? `/pagina/carrusel/eliminar?id_usuario=${selectedId}` : '#'} className="link-dark text-decoration-none px-2" onClick={handleDelete}>
                            <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="#04703F" className="bi bi-trash" viewBox="0 0 16 16">
                                <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5m2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5m3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0z"/>
                                <path d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4zM2.5 3h11V2h-11z"/>
                            </svg>
                        </Link>

                    </div>
                </div>
            <div className="container">
                {carrusel && tamañoOptimo && (
                    <div className="carrusel" style={{ width: tamañoOptimo.width, height: tamañoOptimo.height }}>
                        <img src={carrusel.imagen} alt={carrusel.titulo} />
                    </div>
                )}
                <Link to="/">Volver al inicio</Link>
            </div>
        </div>
    );

}

export default Carrusel;
