import React, { useState, useEffect } from 'react';
import './css/Usuarios.css'; // Asegúrate de importar tus estilos CSS
import { Link } from 'react-router-dom';

function Usuarios() {
    const [datosUsuarios, setDatosUsuarios] = useState([]);
    const [selectedId, setSelectedId] = useState(null);
    const [alturaTitulo, setAlturaTitulo] = useState(70); // Altura inicial del título "Usuarios"

    useEffect(() => {
        // Función para obtener los datos de la API
        const fetchData = async () => {
            try {
                const response = await fetch('URL_DE_TU_API'); // Reemplaza con la URL de tu API
                const data = await response.json();
                setDatosUsuarios(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    const handleRowClick = (id_usuario) => {
        setSelectedId(id_usuario);
    };

    return (
        <>
            <div className="container-fluid">
                <div className="row">
                    <div className="col-md-12">
                        <div className="d-flex justify-content-between align-items-center pt-2 position-absolute top-0 start-0" style={{ marginLeft: '20px', marginTop: `${alturaTitulo}px` }}>
                            <div>
                                <h1>Usuarios</h1>
                            </div>
                        </div>
                        <div className="d-flex justify-content-end pt-2 position-absolute top-0 end-0" style={{ marginRight: '40px' }}>
                            <div className="dropdown">
                                <Link to="#" className="d-block link-dark text-decoration-none" id="dropdownUser1" data-bs-toggle="dropdown" aria-expanded="false">
                                    <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="currentColor" className="bi bi-person-circle" viewBox="0 0 16 16">
                                        <path d="M11 6a3 3 0 1 1-6 0 3 3 0 0 1 6 0" />
                                        <path fillRule="evenodd" d="M0 8a8 8 0 1 1 16 0A8 8 0 0 1 0 8m8-7a7 7 0 0 0-5.468 11.37C3.242 11.226 4.805 10 8 10s4.757 1.225 5.468 2.37A7 7 0 0 0 8 1" />
                                    </svg>
                                </Link>
                                <ul className="dropdown-menu text-small dropdown-menu-end" aria-labelledby="dropdownUser1">
                                    <li><Link className="dropdown-item" to="/menu">Menu</Link></li>
                                    <li><hr className="dropdown-divider" /></li>
                                    <li><Link className="dropdown-item" to="/">Sign out</Link></li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Usuarios;
