import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';
import '../../css/page/Pagina.css';
import CustomNavbar from '../../components/CustomNavbar';

function CarruselInsert() {
    const [newAviso, setNewAviso] = useState({
        url: '',
        estado: '1', // Establecer valor por defecto para estado
    });
    const [selectedFile, setSelectedFile] = useState(null);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewAviso({
            ...newAviso,
            [name]: value
        });
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        setSelectedFile(file);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('file', selectedFile);
        formData.append('URL', newAviso.url);
        formData.append('estado', newAviso.estado);

        try {
            const response = await fetch('http://localhost:8000/aviso/crear', {
                method: 'POST',
                body: formData
            });

            if (response.ok) {
                const data = await response.json();
                alert(`Archivo subido exitosamente: ${data.filename}`);
                window.location.href = '/pagina/carrucel';
            } else {
                const errorData = await response.json();
                alert(`Error al subir el archivo: ${errorData.detail}`);
            }
        } catch (error) {
            console.error('Error al enviar los datos:', error);
            alert('Error al subir el archivo.');
        }
    };

    return (
        <div className="app">
            <CustomNavbar />
            <div className="d-flex align-items-center justify-content-center text-center">
                <h1 className="fs-1">Ingresa un nuevo Logo</h1>
            </div>

            <form onSubmit={handleSubmit} style={{ marginTop: "40px" }}>
                <div id="form-container-input" className="d-flex flex-column align-items-center">
                    {/* Otros campos de entrada */}
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            type="file"
                            id="logo"
                            name="logo"
                            className="fs-2 border-bottom-only no-rounded"
                            onChange={handleFileChange}
                            required
                        />
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            type="text"
                            id="URL"
                            name="url"
                            className="form-control fs-2 border-bottom-only no-rounded"
                            placeholder="URL"
                            value={newAviso.url}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <select
                            id="estado"
                            name="estado"
                            className="fs-2 border-bottom-only no-rounded"
                            value={newAviso.estado}
                            onChange={handleInputChange}
                            required
                        >
                            <option value="1">Activo</option>
                            <option value="0">Inactivo</option>
                        </select>
                    </div>
                </div>
                <div id="form-container-button" className="d-flex align-items-center justify-content-around px-5">
                    <Link to="/pagina/logo" className="btn btn-outline-dark fs-4 btn-lg rounded-pill boton">Cancelar</Link>
                    <button type="submit" className="btn btn-outline-dark fs-4 btn-lg rounded-pill">Guardar</button>
                </div>
            </form>
        </div>
    );
}

export default CarruselInsert;
