import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';
import '../../css/events/Eventos.css';
import CustomNavbar from '../../components/CustomNavbar';

// Importa los SVGs
import { ReactComponent as AddIcon } from '../../assets/add.svg';
import { ReactComponent as RemoveIcon } from '../../assets/remove.svg';

function EncuestaInsert() {
    const [newSurvey, setNewSurvey] = useState({
        titulo: ''
    });

    const [questions, setQuestions] = useState([
        { pregunta: '', pregunta_abierta: 0, pregunta_cerrada_multiple: 0 }
    ]);

    const handleSurveyInputChange = (e) => {
        const { name, value } = e.target;
        setNewSurvey({
            ...newSurvey,
            [name]: value
        });
    };

    const handleQuestionInputChange = (index, e) => {
        const { name, value } = e.target;
        const newQuestions = [...questions];
        newQuestions[index][name] = value;
        setQuestions(newQuestions);
    };

    const addQuestion = () => {
        setQuestions([...questions, { pregunta: '', pregunta_abierta: 0, pregunta_cerrada_multiple: 0 }]);
    };

    const removeQuestion = (index) => {
        const newQuestions = questions.filter((_, i) => i !== index);
        setQuestions(newQuestions);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const surveyResponse = await fetch('http://localhost:8000/encuesta/crear', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newSurvey)
            });

            if (!surveyResponse.ok) {
                throw new Error('Error en la solicitud de encuesta');
            }

            const surveyData = await surveyResponse.json();
            const surveyId = surveyData.id_encuesta; // Asumiendo que el backend devuelve el ID de la nueva encuesta

            for (const question of questions) {
                const questionData = {
                    id_encuesta: surveyId,
                    ...question
                };

                const questionResponse = await fetch('http://localhost:8000/pregunta/crear', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(questionData)
                });

                if (!questionResponse.ok) {
                    throw new Error('Error en la solicitud de pregunta');
                }
            }

            console.log('Encuesta y preguntas creadas exitosamente');
            // Limpiar el formulario despuÃ©s de enviar los datos
            setNewSurvey({ titulo: '' });
            setQuestions([{ pregunta: '', pregunta_abierta: 0, pregunta_cerrada_multiple: 0 }]);
            window.location.href = '/encuestas';
        } catch (error) {
            console.error('Error al enviar los datos:', error);
        }
    };

    return (
        <div className="app">
            <CustomNavbar />
            <div className="d-flex align-items-center justify-content-center text-center">
                <h1 className="fs-1">Ingresa una nueva Encuesta</h1>
            </div>

            <form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
                <div id="form-container-input" className="d-flex flex-column align-items-center">
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            type="text"
                            id="titulo"
                            name="titulo"
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="TÃ­tulo"
                            value={newSurvey.titulo}
                            onChange={handleSurveyInputChange}
                            required
                        />
                    </div>
                    {questions.map((question, index) => (
                        <div key={index} className="w-100">
                            <div className="form-group d-flex py-2 w-100 justify-content-center">
                                <input
                                    type="text"
                                    name="pregunta"
                                    className="fs-2 border-bottom-only no-rounded"
                                    placeholder="Pregunta"
                                    value={question.pregunta}
                                    onChange={(e) => handleQuestionInputChange(index, e)}
                                    required
                                />
                                <button type="button" onClick={() => removeQuestion(index)} className="btn btn-danger mx-2">
                                    <RemoveIcon width="24" height="24" />
                                </button>
                            </div>
                            <div className="form-group d-flex py-2 w-100 justify-content-center">
                                <select
                                    name="pregunta_abierta"
                                    className="fs-2 border-bottom-only no-rounded"
                                    value={question.pregunta_abierta}
                                    onChange={(e) => handleQuestionInputChange(index, e)}
                                    required
                                >
                                    <option value={0}>Cerrada</option>
                                    <option value={1}>Abierta</option>
                                </select>
                            </div>
                            <div className="form-group d-flex py-2 w-100 justify-content-center">
                                <select
                                    name="pregunta_cerrada_multiple"
                                    className="fs-2 border-bottom-only no-rounded"
                                    value={question.pregunta_cerrada_multiple}
                                    onChange={(e) => handleQuestionInputChange(index, e)}
                                    required
                                >
                                    <option value={0}>Ãnica</option>
                                    <option value={1}>MÃºltiple</option>
                                </select>
                            </div>
                        </div>
                    ))}
                    <button type="button" onClick={addQuestion} className="btn btn-success my-3">
                        <AddIcon width="24" height="24" />
                    </button>
                </div>
                <div id="form-container-button" className="d-flex align-items-center justify-content-around px-5">
                    <Link to="/encuestas" className="btn btn-outline-dark fs-4 btn-lg rounded-pill boton">Cancelar</Link>
                    <button type="submit" className="btn btn-outline-dark fs-4 btn-lg rounded-pill">Guardar</button>
                </div>
            </form>
        </div>
    );
}

export default EncuestaInsert;
