import React, { useState, useRef, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, useLocation } from 'react-router-dom';
import CustomNavbar from '../../components/CustomNavbar';

function NoticiaUpdate() {
    const [newNoticia, setNewNoticia] = useState({
        titulo: '',
        resumen: '',
        contenido: '',
        imagen: ''
    });
    const [selectedFile, setSelectedFile] = useState(null);
    const [fileName, setFileName] = useState("");
    const fileInputRef = useRef(null);

    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const noticiaID = searchParams.get('id_noticia');

    useEffect(() => {
        if (noticiaID) {
            fetch(`http://localhost:8000/noticia/${noticiaID}`)
                .then(response => response.json())
                .then(data => {
                    console.log('Datos de laa noticia:', data);
                    const noticiaData = data[0]; // Tomamos el primer objeto de la lista
                    setNewNoticia({
                        titulo: noticiaData.titulo,
                        resumen: noticiaData.resumen,
                        contenido: noticiaData.contenido,
                        imagen: noticiaData.imagen
                    });
                })
                .catch(error => console.error('Error al obtener los datos del aviso:', error));
        }
    }, [noticiaID]);

    const handleFileInputClick = () => {
        fileInputRef.current.click();
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        setSelectedFile(file);
        setFileName(file.name);
        setFileName(file ? file.name : ""); 
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setNewNoticia(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('titulo', newNoticia.titulo);
        formData.append('resumen', newNoticia.resumen);
        formData.append('contenido', newNoticia.contenido);
        formData.append('imagen', selectedFile);

        try {
            const response = await fetch(`http://localhost:8000/noticia/editar/${noticiaID}`, {
                method: 'PUT',
                body: formData
            });

            if (response.ok) {
                const data = await response.json();
                alert(`Archivo subido exitosamente: ${data.filename}`);
                window.location.href = '/pagina/carrusel';
            } else {
                const errorData = await response.json();
                alert(`Error al subir el archivo: ${errorData.detail}`);
            }
        } catch (error) {
            console.error('Error al subir el archivo:', error);
            alert('Error al subir el archivo.');
        }
    };

    return (
        <div className="app">
            <CustomNavbar />
            <div className="d-flex align-items-center justify-content-center text-center">
                <h1 className="fs-1">Ingresa una nueva Noticia</h1>
            </div>

            <form onSubmit={handleSubmit} style={{ marginTop: "30px" }}>
                <div id="form-container-input" className="d-flex flex-column align-items-center">
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            type="text"
                            id="titulo"
                            name="titulo"
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="Titulo"
                            value={newNoticia.titulo}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <button type="button" className="fs-2 border-bottom-only no-rounded" onClick={handleFileInputClick} style={{ width: "100%" }}>
                            Seleccionar archivo
                        </button>
                        <span className="fs-2 border-bottom-only no-rounded">{fileName.length > 15 ? `${fileName.substring(0, 15)}...` : fileName}</span>
                        <input
                            type="file"
                            id="imagen"
                            name="imagen"
                            ref={fileInputRef}
                            style={{ display: 'none' }}
                            className="fs-2 border-bottom-only no-rounded"
                            onChange={handleFileChange}
                            accept=".png, .jpg, .jpeg"
                            required
                        />
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <textarea
                            id="contenido"
                            name="contenido"
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="Escribe tu noticia aquí..."
                            value={newNoticia.contenido}
                            onChange={handleChange}
                            style={{ width: "100%" }}
                            required
                        />
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            id="resumen"
                            type='text'
                            name="resumen"
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="Resumen"
                            value={newNoticia.resumen}
                            onChange={handleChange}
                            required
                        />
                    </div>
                </div>
                <div id="form-container-button" className="d-flex align-items-center justify-content-around px-5">
                    <Link to="/pagina/mapa" className="btn btn-outline-dark fs-4 btn-lg rounded-pill boton">Cancelar</Link>
                    <button type="submit" className="btn btn-outline-dark fs-4 btn-lg rounded-pill">Guardar</button>
                </div>
            </form>
        </div>
    );
}
export default NoticiaUpdate;
