import React, { useState, useEffect } from 'react';
import './css/Usuarios.css'; // Importa tus estilos CSS
import { Link } from 'react-router-dom';

function Usuarios() {
    const [datosUsuarios, setDatosUsuarios] = useState([]);
    const [selectedId, setSelectedId] = useState(null);

    useEffect(() => {
        // FunciÃ³n para obtener los datos de la API
        const fetchData = async () => {
            try {
                const response = await fetch('URL_DE_TU_API'); // Reemplaza con la URL de tu API
                const data = await response.json();
                setDatosUsuarios(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    const handleRowClick = (id_usuario) => {
        setSelectedId(id_usuario);
    };

    return (
        <>
            <div className="px-2">
                <header className="px-5 py-4 mb-3">
                    <div className="container-fluid p-0">
                        <div className="d-flex align-items-center justify-content-between">


                            <div className="dropdown text-end">
                                <Link to="#" className="d-block link-dark text-decoration-none" id="dropdownUser1" data-bs-toggle="dropdown" aria-expanded="false">
                                    <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="currentColor" className="bi bi-person-circle" viewBox="0 0 16 16">
                                        <path d="M11 6a3 3 0 1 1-6 0 3 3 0 0 1 6 0" />
                                        <path fillRule="evenodd" d="M0 8a8 8 0 1 1 16 0A8 8 0 0 1 0 8m8-7a7 7 0 0 0-5.468 11.37C3.242 11.226 4.805 10 8 10s4.757 1.225 5.468 2.37A7 7 0 0 0 8 1" />
                                    </svg>
                                </Link>
                                <ul className="dropdown-menu text-small" aria-labelledby="dropdownUser1">
                                    <li><Link className="dropdown-item" to="/">Menu</Link></li>
                                    <li><hr className="dropdown-divider" /></li>
                                    <li><Link className="dropdown-item" to="#">Sign out</Link></li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </header>
            </div>
            <div className="px-5 py-4">
                <div className="d-flex align-items-center justify-content-between">
                    <p className="fs-1"><b>Usuarios<br /></b></p>
                    <div style={{ display: 'flex' }}>
                        <div className="input-group rounded-pill border border-dark border-1">
                            <input
                                type="search"
                                className="form-control rounded-pill border border-dark border-2 text-center"
                                placeholder="Buscar..."
                                aria-label="Search"
                                aria-describedby="search-addon"
                            />
                        </div>
                        <Link to="/usuarios/insertar" className="d-block link-dark text-decoration-none px-4" id="dropdownUser1" aria-expanded="false">
                            <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" fill="currentColor" className="bi bi-plus-circle" viewBox="0 0 16 16">
                                <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                                <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4" />
                            </svg>
                        </Link>
                        <Link to={selectedId ? `/usuarios/actualizar?id_usuario=${selectedId}` : '#'} className="d-block link-dark text-decoration-none px-1" id="updateLink" aria-expanded="false">
                            <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" fill="currentColor" className="bi bi-pencil-square" viewBox="0 0 16 16">
                                <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293z" />
                                <path d="M13.752 4.396l-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z" />
                                <path fillRule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5z" />
                            </svg>
                        </Link>
                        <Link to={selectedId ? `/usuarios/eliminar?id_usuario=${selectedId}` : '#'} className="d-block link-dark text-decoration-none px-1" id="deleteLink" aria-expanded="false">
                            <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" fill="currentColor" className="bi bi-trash" viewBox="0 0 16 16">
                                <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5m2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5m3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0z" />
                                <path d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h4.5a1 1 0 0 1 1 1v1m-1-1H2v1h12V2z" />
                            </svg>
                        </Link>
                    </div>
                </div>
                <div className="table-responsive mt-3 border border-dark border-2 rounded-3 overflow-y-auto">
                    <table className="table table-bordered">
                        <thead>
                            <tr>
                                <th>Area</th>
                                <th>Nombre</th>

                            </tr>
                        </thead>
                        <tbody>
                            {datosUsuarios.map((usuario) => (
                                <tr
                                    key={usuario.id_usuario}
                                    onClick={() => handleRowClick(usuario.id_usuario)}
                                    className={selectedId === usuario.id_usuario ? 'selected' : ''}
                                >
                                    <td>{usuario}</td>
                                    <td>{usuario.nombre_usuario}</td>

                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default Usuarios;
