import React, { useState, use } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';
import '../../css/page/Pagina.css';
import CustomNavbar from '../../components/CustomNavbar';

function PaginaInsert() {
    const [newUser, setNewUser] = useState({
        area: '',
        nombre: '',
        contrasena: '',
        estado: '1', // Establecer valor por defecto para estado
        permisos: '1' // Establecer valor por defecto para permisos (anteriormente rol)
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewUser({
            ...newUser,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        const fileInputRef = useRef(null);

        e.preventDefault();
        try {
            const response = await fetch('http://localhost:8000/usuario/crear', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newUser)
            });

            if (!response.ok) {
                throw new Error('Error en la solicitud');
            }

            const data = await response.json();
            console.log('Respuesta de la API:', data);
            // Limpiar el formulario después de enviar los datos
            setNewUser({
                area: '',
                nombre: '',
                contrasena: '',
                estado: '1',
                permisos: '1'
            });
            window.location.href = '/usuarios';
        } catch (error) {
            console.error('Error al enviar los datos:', error);
        }
    };
    const handleFileInputClick = () => {
        fileInputRef.current.click();
    };

    return (
        <div className="app">
            <CustomNavbar />
            <div className="d-flex  align-items-center justify-content-center text-center">
                <h1 className="fs-1">Ingresa un nuevo Logo</h1>
            </div>

            <form onSubmit={handleSubmit} style={{marginTop: "10px"}}>
                <div id="form-container-input" className="d-flex flex-column align-items-center">
                    {/* Otros campos de entrada */}
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <button className="custom-file-button" onClick={handleFileInputClick}>Seleccionar archivo</button>
                        <input
                            type="file"
                            id="area"
                            name="area"
                            ref={fileInputRef}
                            style={{ display: 'none' }}
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="Área"
                            value={newUser.area}
                            onChange={handleInputChange}
                            required
                        />
                    </div>

                </div>
                <div id="form-container-button" className="d-flex align-items-center justify-content-around px-5">
                    <Link to="/usuarios" className="btn btn-outline-dark fs-4 btn-lg rounded-pill boton">Cancelar</Link>
                    <button type="submit" className="btn btn-outline-dark fs-4 btn-lg rounded-pill">Guardar</button>
                </div>
            </form>
        </div>
    );
}

export default PaginaInsert;
