import React, { useState, useEffect } from 'react'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';
import { Link } from 'react-router-dom'; 
import '../../css/user/Usuarios.css';

function Usuarios() {
    const [datosUsuarios, setDatosUsuarios] = useState([]);
    const [selectedIds, setSelectedIds] = useState(new Set());

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:8000/listar_usuarios');
                const data = await response.json();
                // Mapear los datos para ajustar el formato de 'estado' y 'permisos'
                const usuariosConValoresString = data.map(usuario => ({
                    ...usuario,
                    estado: usuario.estado === '0' ? 'Inactivo' : 'Activo',
                    permisos: usuario.permisos === '0' ? 'Funcionario' : 'Administrador'
                }));
                setDatosUsuarios(usuariosConValoresString);
            } catch (error) {
                console.error('Error al obtener los datos:', error);
            }
        };

        fetchData();
    }, []);


    // Cambia el nombre de la variable selectedId a selectedIds
    const handleRowClick = (id_usuario) => {
        const updatedSelectedIds = new Set(selectedIds);
        if (updatedSelectedIds.has(id_usuario)) {
            updatedSelectedIds.delete(id_usuario);
        } else {
            updatedSelectedIds.add(id_usuario);
        }
        setSelectedIds(updatedSelectedIds);
    };




    return (
        <div className="app">
            <Navbar bg="white" expand="lg" fixed="top">
                <Nav className="ml-auto" style={{ marginRight: '10px'}}>
                    <NavDropdown 
                    title={
                        <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="#04703F" className="bi bi-person-circle" viewBox="0 0 16 16">
                        <path d="M11 6a3 3 0 1 1-6 0 3 3 0 0 1 6 0"/>
                        <path fillRule="evenodd" d="M0 8a8 8 0 1 1 16 0A8 8 0 0 1 0 8m8-7a7 7 0 0 0-5.468 11.37C3.242 11.226 4.805 10 8 10s4.757 1.225 5.468 2.37A7 7 0 0 0 8 1"/>
                        </svg>
                    } 
                    id="user-menu"
                    alignRight
                    className="custom-dropdown-menu"
                    style={{ marginRight: '30px'}}
                    >
                    <NavDropdown.Item as={Link} to="/menu">Menu</NavDropdown.Item>
                    <NavDropdown.Divider />
                    <NavDropdown.Item as={Link} to="/">Salir</NavDropdown.Item>
                    </NavDropdown>
                </Nav>
            </Navbar>

            <div className="acontainer">
                <div className="container d-flex justify-content-between align-items-center">
                    <h1 className="fs-1"><b>Usuarios</b></h1>
                    <div className="d-flex align-items-center">
                        <div className="input-group rounded-pill border border-1 me-2 custom-border">
                            <input
                                type="search"
                                className="form-control rounded-pill border border-2 text-center custom-border"
                                placeholder="Buscar..."
                                aria-label="Buscar"
                                aria-describedby="search-addon"
                                style={{ color: "#04703F"}}
                            />
                        </div>
                        <Link to="/usuarios/insertar" className="link-dark text-decoration-none px-2">
                            <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="#04703F" className="bi bi-plus-circle" viewBox="0 0 16 16">
                                <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                                <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4" />
                            </svg>
                        </Link>
                        <Link to={selectedIds ? `/usuarios/actualizar?id_usuario=${selectedIds}` : '#'} className="link-dark text-decoration-none px-2">
                            <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="#04703F" className="bi bi-pencil-square" viewBox="0 0 16 16">
                                <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293z" />
                                <path d="M13.752 4.396l-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z" />
                                <path fillRule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5z" />
                            </svg>
                        </Link>
                        <Link to={selectedIds ? `/usuarios/eliminar?id_usuario=${selectedId}` : '#'} className="link-dark text-decoration-none px-2">
                            <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="#04703F" className="bi bi-trash" viewBox="0 0 16 16">
                                <path d="M5.5 5.5A.5.5 0 0 1 6 6v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5m2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V6a.5.5 0 0 1 .5-.5m3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0z"/>
                                <path d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4zM2.5 3h11V2h-11z"/>
                            </svg>
                        </Link>
                    </div>
                </div>
            </div>
            <div id="tabla-container" class="px-4 py-4">
                <table className="table table-hover" style={{ backgroundColor: "transparent", borderCollapse: "separate", borderSpacing: "0 8px" }}>
                    <thead>
                        <tr style={{ borderBottom: "2px solid #04703F" }}>
                            <th scope="col" className="fs-3" style={{ backgroundColor: "#FDFBF6", borderBottom: "none", color: "#04703F" }}>Area</th>
                            <th scope="col" className="fs-3" style={{ backgroundColor: "#FDFBF6", borderBottom: "none", color: "#04703F" }}>Nombre</th>
                            <th scope="col" className="fs-3" style={{ backgroundColor: "#FDFBF6", borderBottom: "none", color: "#04703F" }}>Estado</th>
                            <th scope="col" className="fs-3" style={{ backgroundColor: "#FDFBF6", borderBottom: "none", color: "#04703F" }}>Rol</th>

                        </tr>
                    </thead>
                    <tbody>
                        {datosUsuarios.map((usuario) => (
                        <tr
                            key={usuario.id}
                            onClick={() => handleRowClick(usuario.id)}
                            className={selectedIds.has(usuario.id) ? 'selected' : ''}
                            style={{ borderBottom: "2px solid #000", cursor: "pointer" }}
                        >
                            <td className='fs-4' style={{ borderBottom: "2px solid #04703F", color: "#04703F", fontWeight: 'bold' }}>{usuario.area}</td>
                            <td className='fs-4' style={{ borderBottom: "2px solid #04703F", color: "#04703F", fontWeight: 'bold' }}>{usuario.nombre}</td>
                            <td className='fs-4' style={{ borderBottom: "2px solid #04703F", color: "#04703F", fontWeight: 'bold' }}>{usuario.estado}</td>
                            <td className='fs-4' style={{ borderBottom: "2px solid #04703F", color: "#04703F", fontWeight: 'bold' }}>{usuario.permisos}</td>
                        </tr>
                    ))}

                    </tbody>
                </table>
            </div>

        </div>
    );
}

export default Usuarios;
