import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Form } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import '../../css/user/Usuarios.css';

function UserUpdate() {
    const [newUser, setNewUser] = useState({
        area: '',
        nombre: '',
        contrasena: '',
        estado: '1',
        permisos: '1'
    });

    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const userID = searchParams.get('id_usuario');

    useEffect(() => {
        if (userID) {
            fetch(`http://localhost:8000/detalle_usuario/${userID}`)
                .then(response => response.json())
                .then(data => {
                    console.log('Datos del usuario:', data); // Agregamos un log aquí para verificar los datos del usuario
                    setNewUser(data);
                })
                .catch(error => console.error('Error al obtener los datos del usuario:', error));
        }
    }, [userID]);

    console.log('Nuevo usuario:', newUser); // Agregamos un log aquí para verificar el estado del nuevo usuario

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewUser({
            ...newUser,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:8000/editar_usuario/${userID}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newUser)
            });

            if (!response.ok) {
                throw new Error('Error en la solicitud');
            }

            const data = await response.json();
            console.log('Respuesta de la API:', data);
            setNewUser({
                area: '',
                nombre: '',
                contrasena: '',
                estado: '1',
                permisos: '1'
            });
            window.location.href = '/usuarios';
        } catch (error) {
            console.error('Error al enviar los datos:', error);
        }
    };

    return (
        <div className="app">
            <Navbar bg="white" expand="lg" fixed="top">
                {/* Navbar contents */}
            </Navbar>

            <div className="d-flex align-items-center justify-content-center text-center">
                <h1 className="fs-1">Edita a tu usuario Usuario</h1>
            </div>

            <Form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
                {/* Formulario y botones */}
            </Form>
        </div>
    );
}

export default UserUpdate;
