import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';
import CustomNavbar from '../../components/CustomNavbar';

function NoticiaInsert() {
    const [newNoticia, setNewNoticia] = useState({
        titulo: '',
        resumen: '',
        contenido: '',
        imagen: ''
    });

    const handleChange = (event) => {
        setNewNoticia({
            ...newNoticia,
            [event.target.name]: event.target.value
        });
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch('http://localhost:8000/noticia/crear', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newNoticia)
            });

            if (!response.ok) {
                throw new Error('Error en la solicitud');
            }

            const data = await response.json();
            console.log('Respuesta de la API:', data);
            setNewNoticia({
                titulo: '',
                resumen: '',
                contenido: '',
                imagen: ''
            });
            window.location.href = '/noticias';
        } catch (error) {
            console.error('Error al enviar los datos:', error);
        }
    }

    return (
        <div className="app">
            <CustomNavbar />
            <div className="d-flex align-items-center justify-content-center text-center">
                <h1 className="fs-1">Ingresa una nueva Ubicacion</h1>
            </div>

            <form onSubmit={handleSubmit} style={{ marginTop: "40px" }}>
                <div id="form-container-input" className="d-flex flex-column align-items-center">
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            type="text"
                            id="lugar"
                            name="lugar"
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="Lugar"
                            value={newNoticia.lugar}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            type="text"
                            id="latitud"
                            name="latitud"
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="Latitud"
                            value={newNoticia.latitud}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            type="text"
                            id="longitud"
                            name="longitud"
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="Longitud"
                            value={newNoticia.longitud}
                            onChange={handleChange}
                            required
                        />
                    </div>
                </div>
                <div id="form-container-button" className="d-flex align-items-center justify-content-around px-5">
                    <Link to="/pagina/mapa" className="btn btn-outline-dark fs-4 btn-lg rounded-pill boton">Cancelar</Link>
                    <button type="submit" className="btn btn-outline-dark fs-4 btn-lg rounded-pill">Guardar</button>
                </div>
            </form>
        </div>
    );
}

export default NoticiaInsert
