import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Form } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import '../../css/user/Usuarios.css';

function UserUpdate() {
    const [user, setUser] = useState({
        area: '',
        nombre: '',
        contrasena: '',
        estado: '1',
        permisos: '1'
    });

    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const userID = searchParams.get('id_usuario');

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await fetch(`http://localhost:8000/detalle_usuario/${userID}`);
                if (!response.ok) {
                    throw new Error('Error al obtener los datos del usuario');
                }
                const userData = await response.json();
                setUser(userData);
            } catch (error) {
                console.error(error);
            }
        };

        if (userID) {
            fetchUserData();
        }
    }, [userID]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUser({
            ...user,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:8000/editar_usuario/${userID}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(user)
            });

            if (!response.ok) {
                throw new Error('Error en la solicitud');
            }

            const data = await response.json();
            console.log('Respuesta de la API:', data);
            setUser({
                area: '',
                nombre: '',
                contrasena: '',
                estado: '1',
                permisos: '1'
            });
            window.location.href = '/usuarios';
        } catch (error) {
            console.error('Error al enviar los datos:', error);
        }
    };

    return (
        <div className="app">
            <Navbar bg="white" expand="lg" fixed="top">
                {/* Navbar contents */}
            </Navbar>

            <div className="d-flex align-items-center justify-content-center text-center">
                <h1 className="fs-1">Edita a tu usuario</h1>
            </div>

            <Form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
                <Form.Group controlId="area">
                    <Form.Label>Área</Form.Label>
                    <Form.Control
                        type="text"
                        name="area"
                        value={user.area}
                        onChange={handleInputChange}
                        required
                    />
                </Form.Group>
                <Form.Group controlId="nombre">
                    <Form.Label>Nombre</Form.Label>
                    <Form.Control
                        type="text"
                        name="nombre"
                        value={user.nombre}
                        onChange={handleInputChange}
                        required
                    />
                </Form.Group>
                <Form.Group controlId="contrasena">
                    <Form.Label>Contraseña</Form.Label>
                    <Form.Control
                        type="password"
                        name="contrasena"
                        value={user.contrasena}
                        onChange={handleInputChange}
                        required
                    />
                </Form.Group>
                <Form.Group controlId="estado">
                    <Form.Label>Estado</Form.Label>
                    <Form.Control
                        as="select"
                        name="estado"
                        value={user.estado}
                        onChange={handleInputChange}
                        required
                    >
                        <option value="1">Activo</option>
                        <option value="0">Inactivo</option>
                    </Form.Control>
                </Form.Group>
                <Form.Group controlId="permisos">
                    <Form.Label>Permisos</Form.Label>
                    <Form.Control
                        as="select"
                        name="permisos"
                        value={user.permisos}
                        onChange={handleInputChange}
                        required
                    >
                        <option value="1">Administrador</option>
                        <option value="0">Funcionario</option>
                    </Form.Control>
                </Form.Group>
                <div className="d-flex justify-content-between">
                    <Link to="/usuarios" className="btn btn-outline-dark">Cancelar</Link>
                    <button type="submit" className="btn btn-dark">Guardar</button>
                </div>
            </Form>
        </div>
    );
}

export default UserUpdate;
