import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, NavDropdown, Form } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import '../../css/user/Usuarios.css';

function UserUpdate() {
    const [newUser, setNewUser] = useState({
        area: '',
        nombre: '',
        contrasena: '',
        estado: '1',
        permisos: '1'
    });

    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const userID = searchParams.get('id_usuario');

    useEffect(() => {
        if (userID) {
            fetch(`http://localhost:8000/detalle_usuario/${userID}`)
                .then(response => response.json())
                .then(data => {
                    setNewUser(data);
                })
                .catch(error => console.error('Error al obtener los datos del usuario:', error));
        }
    }, [userID]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewUser({
            ...newUser,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:8000/editar_usuario/${userID}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newUser)
            });

            if (!response.ok) {
                throw new Error('Error en la solicitud');
            }

            const data = await response.json();
            console.log('Respuesta de la API:', data);
            setNewUser({
                area: '',
                nombre: '',
                contrasena: '',
                estado: '1',
                permisos: '1'
            });
            window.location.href = '/usuarios';
        } catch (error) {
            console.error('Error al enviar los datos:', error);
        }
    };

    return (
        <div className="app">
            <Navbar bg="white" expand="lg" fixed="top">
                {/* Navbar contents */}
            </Navbar>

            <div className="d-flex align-items-center justify-content-center text-center">
                <h1 className="fs-1">Edita a tu usuario Usuario</h1>
            </div>

            <Form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
                <div id="form-container-input" className="d-flex flex-column align-items-center">
                    <Form.Group controlId="area">
                        <Form.Control
                            type="text"
                            name="area"
                            placeholder="Área"
                            value={newUser.area}
                            onChange={handleInputChange}
                            required
                        />
                    </Form.Group>
                    <Form.Group controlId="nombre">
                        <Form.Control
                            type="text"
                            name="nombre"
                            placeholder="Nombre"
                            value={newUser.nombre}
                            onChange={handleInputChange}
                            required
                        />
                    </Form.Group>
                    <Form.Group controlId="contrasena">
                        <Form.Control
                            type="password"
                            name="contrasena"
                            placeholder="Contraseña"
                            value={newUser.contrasena}
                            onChange={handleInputChange}
                            required
                        />
                    </Form.Group>
                    <Form.Group controlId="estado">
                        <Form.Control
                            as="select"
                            name="estado"
                            value={newUser.estado}
                            onChange={handleInputChange}
                            required
                        >
                            <option value="1">Activo</option>
                            <option value="0">Inactivo</option>
                        </Form.Control>
                    </Form.Group>
                    <Form.Group controlId="permisos">
                        <Form.Control
                            as="select"
                            name="permisos"
                            value={newUser.permisos}
                            onChange={handleInputChange}
                            required
                        >
                            <option value="1">Administrador</option>
                            <option value="0">Funcionario</option>
                        </Form.Control>
                    </Form.Group>
                </div>
                <div id="form-container-button" className="d-flex align-items-center justify-content-around px-5">
                    <Link to="/usuarios" className="btn btn-outline-dark fs-4 btn-lg rounded-pill boton">Cancelar</Link>
                    <button type="submit" className="btn btn-outline-dark fs-4 btn-lg rounded-pill">Guardar</button>
                </div>
            </Form>
        </div>
    );
}

export default UserUpdate;
