import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import '../../css/user/Usuarios.css';

function UserUpdate() {
    const [newUser, setNewUser] = useState({
        area: '',
        nombre: '',
        contrasena: '',
        estado: '1',
        permisos: '1'
    });

    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const userID = searchParams.get('id_usuario');

    useEffect(() => {
        if (userID) {
            fetch(`http://localhost:8000/detalle_usuario/${userID}`)
                .then(response => response.json())
                .then(data => {
                    console.log('Datos del usuario:', data);
                    const userData = data[0]; // Tomamos el primer objeto de la lista
                    setNewUser({
                        area: userData.area,
                        nombre: userData.nombre,
                        contrasena: '', // No se proporciona desde la API
                        estado: userData.estado,
                        permisos: userData.permisos
                    });
                })
                .catch(error => console.error('Error al obtener los datos del usuario:', error));
        }
    }, [userID]);


    console.log('Nuevo usuario:', newUser); // Agregamos un log aquÃ­ para verificar el estado del nuevo usuario

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewUser({
            ...newUser,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:8000/editar_usuario/${userID}`, {
                method: 'PUT', // Cambiar a PUT en lugar de POST
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newUser)
            });

            if (!response.ok) {
                throw new Error('Error en la solicitud');
            }

            const data = await response.json();
            console.log('Respuesta de la API:', data);
            setNewUser({
                area: '',
                nombre: '',
                contrasena: '',
                estado: '1',
                permisos: '1'
            });
            window.location.href = '/usuarios';
        } catch (error) {
            console.error('Error al enviar los datos:', error);
        }
    };    


    return (
        <div className="app">


            <div className="d-flex  align-items-center justify-content-center text-center">
                <h1 className="fs-1">Edita a tu usuario Usuario</h1>
            </div>

            <form onSubmit={handleSubmit} style={{marginTop: "10px"}}>
                <div id="form-container-input" className="d-flex flex-column align-items-center">
                    {/* Otros campos de entrada */}
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            type="text"
                            id="area"
                            name="area"
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="Ãrea"
                            value={newUser.area}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            type="text"
                            id="nombre"
                            name="nombre"
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="Nombre"
                            value={newUser.nombre}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <input
                            type="password"
                            id="contrasena"
                            name="contrasena"
                            className="fs-2 border-bottom-only no-rounded"
                            placeholder="ContraseÃ±a"
                            value={newUser.contrasena}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <select
                            id="estado"
                            name="estado"
                            className="fs-2 border-bottom-only no-rounded"
                            value={newUser.estado}
                            onChange={handleInputChange}
                            required
                        >
                            <option value="1">Activo</option>
                            <option value="0">Inactivo</option>
                        </select>
                    </div>
                    <div className="form-group d-flex py-2 w-100 justify-content-center">
                        <select
                            id="permisos"
                            name="permisos"
                            className="fs-2 border-bottom-only no-rounded"
                            value={newUser.permisos}
                            onChange={handleInputChange}
                            required
                        >
                            <option value="1">Administrador</option>
                            <option value="0">Funcionario</option>
                        </select>
                    </div>
                </div>
                <div id="form-container-button" className="d-flex align-items-center justify-content-around px-5">
                    <Link to="/usuarios" className="btn btn-outline-dark fs-4 btn-lg rounded-pill boton">Cancelar</Link>
                    <button type="submit" className="btn btn-outline-dark fs-4 btn-lg rounded-pill">Guardar</button>
                </div>
            </form>
        </div>
    );
}

export default UserUpdate;
