import React, { useState, useEffect } from 'react'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom'; 
import '../../css/user/Usuarios.css';
import CustomNavbar from '../../components/CustomNavbar';

function Pagina() {
    const [logo, setLogo] = useState(null);

    useEffect(() => {
        const fetchLogo = async () => {
            try {
                const response = await fetch('http://localhost:8000/logo');
                const data = await response.json();
                if (data && data.length > 0) {
                    setLogo(data[0]); // Asumimos que solo hay un logo activo
                }
            } catch (error) {
                console.error('Error al obtener el logo:', error);
            }
        };

        fetchLogo();
    }, []);

    return (
        <div className="app">
            <CustomNavbar />
            <div className="acontainer">
                <div className="container d-flex justify-content-between align-items-center">
                    <h1 className="fs-1"><b>Logo</b></h1>
                    <div className="d-flex align-items-center">
                        <div className="input-group rounded-pill border border-1 me-2 custom-border">
                            <input
                                type="search"
                                className="form-control rounded-pill border border-2 text-center custom-border"
                                placeholder="Buscar..."
                                aria-label="Buscar"
                                aria-describedby="search-addon"
                                style={{ color: "#04703F"}}
                            />
                        </div>
                        <Link to="/pagina/insertar" className="link-dark text-decoration-none px-2">
                            <svg xmlns="http://www.w3.org/2000/svg" width="54" height="54" fill="#04703F" className="bi bi-plus-circle" viewBox="0 0 16 16">
                                <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                                <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4" />
                            </svg>
                        </Link>
                    </div>
                </div>
            </div>
            <div id="logo-container" className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
                {logo ? (
                    <img src={`http://localhost:8000/${logo.ruta}${logo.archivo}`} alt="Logo" style={{ maxWidth: '20%', height: 'auto', marginTop: '12%'}} />
                ) : (
                    <p>No hay un logo disponible.</p>
                )}
            </div>
        </div>
    );
}

export default Pagina;
